import Main from '../../components/Main'
import {useRouter} from 'next/router'

export default function Articles(){

    const router = useRouter();
    const {id} = router.query; 
    var i;
    for(i = 0; i <= 3; i++){

        if(Main.article[i].id == id){

           var article = Main.article[i];
        }
    }

    return(

        <div>
        <h2>{ article.title }</h2>
        
        </div>
    )

}
